mapper.select:
SELECT RANK() OVER(ORDER BY WIN_POINT DESC) AS RANKING, A.SEASON_CODE, A.TEAM_CODE, B.TEAM_NAME , A.WIN_POINT, A.GAME_COUNT, A.WIN_COUNT, A.LOSE_COUNT FROM ( SELECT SEASON_CODE, TEAM_CODE, SUM(WIN_POINT) AS WIN_POINT, SUM(GAME_COUNT) AS GAME_COUNT, SUM(WIN_COUNT) AS WIN_COUNT, SUM(LOSE_COUNT) AS LOSE_COUNT FROM team_score WHERE SEASON_CODE = #{season}
AND GAME_ROUND = #{round}
GROUP BY TEAM_CODE, SEASON_CODE ) A LEFT JOIN team B ON A.TEAM_CODE = B.TEAM_CODE
